BEGIN;

CREATE OR REPLACE FUNCTION C_FLIGHT() RETURNS VOID AS $$
DECLARE

  -- システム日付-90日間のフライト情報を登録
  startDate DATE := current_date-90;
  endDate DATE := current_date;

  -- 登録期間を明示的に指定する場合
  --startDate DATE := '2014-10-01';
  --endDate DATE := '2014-12-31';
  
  currentDate DATE := startDate;
  
  i INT;
  
BEGIN
  i := 0;
  WHILE currentDate <= endDate LOOP
    
    
    currentDate := currentDate + 1;
    i := i + 1;
    
  END LOOP;
END;
$$ LANGUAGE plpgsql;

SELECT C_FLIGHT();

DROP FUNCTION C_FLIGHT();

COMMIT;
